<script>
// Desktop Search functionality
document.addEventListener('DOMContentLoaded', function() {
    const desktopSearchInput = document.getElementById('desktopSearchInput');
    const desktopSearchSuggestions = document.getElementById('desktopSearchSuggestions');
    const desktopSuggestionsList = document.getElementById('desktopSuggestionsList');
    let desktopSearchTimeout;

    if (desktopSearchInput) {
        desktopSearchInput.addEventListener('input', function() {
            const query = this.value.trim();

            // Clear previous timeout
            clearTimeout(desktopSearchTimeout);

            if (query.length < 2) {
                desktopSearchSuggestions.classList.add('hidden');
                return;
            }

            // Debounce search requests
            desktopSearchTimeout = setTimeout(() => {
                fetchDesktopSearchSuggestions(query);
            }, 300);
        });

        // Hide suggestions when clicking outside
        document.addEventListener('click', function(e) {
            if (!desktopSearchInput.contains(e.target) && !desktopSearchSuggestions.contains(e.target)) {
                desktopSearchSuggestions.classList.add('hidden');
            }
        });

        // Handle suggestion clicks
        desktopSuggestionsList.addEventListener('click', function(e) {
            if (e.target.classList.contains('desktop-suggestion-item')) {
                const productName = e.target.textContent;
                desktopSearchInput.value = productName;
                desktopSearchSuggestions.classList.add('hidden');
                document.getElementById('desktopSearchForm').submit();
            }
        });
    }

    function fetchDesktopSearchSuggestions(query) {
        fetch('http://192.168.1.77:8000/products/searchSuggestions?q=' + encodeURIComponent(query), {
            method: 'GET',
            headers: {
                'X-Requested-With': 'XMLHttpRequest'
            }
        })
        .then(response => response.json())
        .then(data => {
            if (data.success && data.suggestions.length > 0) {
                displayDesktopSuggestions(data.suggestions);
            } else {
                desktopSearchSuggestions.classList.add('hidden');
            }
        })
        .catch(error => {
            console.error('Desktop search suggestions error:', error);
            desktopSearchSuggestions.classList.add('hidden');
        });
    }

    function displayDesktopSuggestions(suggestions) {
        desktopSuggestionsList.innerHTML = '';

        suggestions.forEach(suggestion => {
            const item = document.createElement('div');
            item.className = 'desktop-suggestion-item px-4 py-2 hover:bg-gray-100 cursor-pointer text-sm text-gray-700';
            item.textContent = suggestion.name;
            desktopSuggestionsList.appendChild(item);
        });

        desktopSearchSuggestions.classList.remove('hidden');
    }
});

// Simple toast function
function showToast(message, type = 'success') {
    const toast = document.createElement('div');
    toast.className = `p-4 rounded-lg text-white text-sm ${type === 'success' ? 'bg-green-500' : 'bg-red-500'}`;
    toast.textContent = message;

    const container = document.getElementById('toastContainer');
    container.appendChild(toast);

    setTimeout(() => {
        toast.remove();
    }, 3000);
}

// Ensure logo is visible on desktop
document.addEventListener('DOMContentLoaded', function() {
    const logoElement = document.querySelector('header a[href="http://192.168.1.77:8000"]');
    if (logoElement) {
        logoElement.style.display = 'flex';
        logoElement.style.alignItems = 'center';
        logoElement.style.gap = '0.75rem';
    }
});
</script>
