                    body: JSON.stringify({
                        persistent_token: persistentToken
                    })
                })
                .then(response => response.json())
                .then(data => {
                    if (data.success) {
                        // Reload page to restore session
                        window.location.reload();
                    }
                })
                .catch(error => {
